import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import CartItem from "../components/CartItem";
import { useEffect, useState } from "react";



const Cart = () => {

  const {cart}= useSelector((state)=>state);
  const [totalAmount,setTotalmount]= useState(0);

  useEffect(()=> {

      setTotalmount( cart.reduce((acc, curr)=> acc +curr.price, 0) );

  },[cart])

  return (
    <div>
 {
   cart.length > 0 ? (
    <div>
     <div>
     {
      cart.map((item, index)=>{
         return <CartItem key={item.id} item= {item} itemIndex= {index} />
      })
     }
         
     </div>

     <div>
        <div>
            <div>Your Cart</div>
            <div>Summary</div>
            <p>
              <span>Total Item : {cart.length}</span>
            </p>
        </div>

        <div>
          <p>Total Amount: ${totalAmount}</p>
          <button>
            CheckOut Now
          </button>
        </div>
     </div>

   </div>) :
   (
    <div>
       <h1>Card Empty</h1>
       <Link to={"/"}>
         <button>
             Shop Now
         </button>
       </Link>
    </div>
   )
 }
    </div>
  );
};

export default Cart;
