import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import CartItem from "../components/CartItem";

const Cart = () => {
  const { cart } = useSelector((state) => state);
  console.log("Printing Cart");
  console.log(cart);
  const [totalAmount, setTotalAmount] = useState(0);

  useEffect(() => {
    setTotalAmount(cart.reduce((acc, curr) => acc + curr.price, 0));
  }, [cart]);

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4 md:px-16 lg:px-32">
      {cart.length > 0 ? (
        <div className="flex flex-col gap-8">
          {/* Cart Items */}
          <div className="flex flex-col gap-4">
            {cart.map((item, index) => (
              <CartItem key={item.id} item={item} itemIndex={index} />
            ))}
          </div>

          {/* Cart Summary */}
          <div className="bg-white shadow-md rounded-lg p-6">
            <div className="flex justify-between items-center border-b pb-4">
              <h2 className="text-xl font-semibold text-gray-800">Your Cart</h2>
              <h2 className="text-xl font-semibold text-green-700">Summary</h2>
            </div>
            <p className="mt-4 text-gray-600">
              <span className="font-medium">Total Items:</span> {cart.length}
            </p>
            <div className="flex justify-between items-center mt-6">
              <p className="text-xl font-semibold text-gray-800">
                Total Amount: <span className="text-green-700">${totalAmount}</span>
              </p>
              <button className="bg-green-600 text-white px-6 py-2 rounded-lg hover:bg-green-700 transition">
                Checkout Now
              </button>
            </div>
          </div>
        </div>
      ) : (
        <div className="flex flex-col items-center justify-center gap-4 mt-12">
          <h1 className="text-2xl font-bold text-gray-700">Cart Empty</h1>
          <Link to={"/"}>
            <button className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition">
              Shop Now
            </button>
          </Link>
        </div>
      )}
    </div>
  );
};

export default Cart;
