import { FcDeleteDatabase } from "react-icons/fc";
import { useDispatch } from "react-redux";
import { toast } from "react-hot-toast";
import { remove } from "../redux/Slices/cartSlice";

const CartItem = ({ item, itemIndex }) => {
  const dispatch = useDispatch();

  const removeFromCart = () => {
    dispatch(remove(item.id));
    toast.error("Item Removed");
  };

  return (
    <div className="flex items-center gap-4 border-b py-4">
      {/* Product Image */}
      <div className="w-24">
        <img src={item.image} alt={item.title} className="w-full h-auto rounded-md" />
      </div>

      {/* Product Details */}
      <div className="flex flex-col flex-grow">
        <h1 className="text-lg font-semibold text-gray-800">{item.title}</h1>
        <p className="text-sm text-gray-600 truncate">{item.description}</p>

        <div className="flex items-center justify-between mt-4">
          {/* Price */}
          <p className="text-lg font-semibold text-green-700">${item.price}</p>

          {/* Delete Button */}
          <div
            className="p-2 bg-red-100 rounded-full cursor-pointer hover:bg-red-200"
            onClick={removeFromCart}
          >
            <FcDeleteDatabase size={24} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartItem;
